import { useSelector, useDispatch } from 'react-redux'
import store from './store'
import { addProduct, removeProduct } from '../feature/product/productSlice'


const ProductStore = () => {

    const product = useSelector((state) => state.value)
    const dispatch = useDispatch()

// Can still subscribe to the store
//store.subscribe(() => console.log(store.getState()))

// Still pass action objects to `dispatch`, but they're created for us
store.dispatch(addProduct({'productName' : 'Men T Shirt'}))

store.dispatch(addProduct({'productName' : 'Jeans'}))

store.dispatch(removeProduct({'productName' : 'Jeans'}))

return (<b>Products in the Store</b>)
} 
export default ProductStore
