import {useState} from 'react'
const EditProductForm = () => {

    const {name, setName} = useState("")
    const {description, setDescription} = useState("")
    const {category, setCategory} = useState("")
    const {expiry, setExpiry} = useState(false)
    const {price, setPrice} = useState("£")
    const {special, setSpecial} = useState(false) 

    const updatePrice = (e) => {
        setPrice(e.target.value)
    }

    return (
        <div> 

            <heading>Edit Product Form</heading>

            <div className="formInput">
                <table>
                <tr><td>Name</td><td><input type="text" name="name" value={name}/></td></tr>
                <tr><td>Description</td><td><input type="text" name="description" value={description}/></td></tr>
                <tr><td>Category</td><td><input type="text" name="category" value={category}/></td></tr>
                <tr><td>Expiry</td><td><input type="Text"/></td></tr>
                <tr><td>Price</td><td><input type="Text" placeholder="£" onChange={(e)=> updatePrice(e)} value={price}/></td></tr>
                <tr><td>Is Special Product ?</td><td><input type="radio" id="special"/><input type="radio" id="nospeical"/></td></tr>


                </table>
                <button className="addButton">Add</button> <button>Cancel</button>

            </div>
        </div>
    )
}
export default EditProductForm
