import { useSelector, useDispatch } from 'react-redux'
//import store from './store'
import { addProduct, removeProduct } from '../feature/product/productSlice'


const ProductStore = () => {

    const products = useSelector((state) => state.products)
    const dispatch = useDispatch()

    //console.log(products)

// Can still subscribe to the store
//store.subscribe(() => console.log(store.getState()))

// Still pass action objects to `dispatch`, but they're created for us


const addProductClicked = () => {

        dispatch(addProduct( { id: '9', productName: 'Mens T Shirt' }))   
  }

const deleteProductClick = (index) => {

    dispatch(removeProduct(index))   
}

const getCategory = () => {
    const categories = [];
    products.map(product => {
    if (categories.indexOf(product.category) === -1) {
        categories.push(product.category)
    }
    });
    return categories;
}

const handleChange = (e) => {
    console.log(e.target.value);
  }

return (<div><b>Products in the Store</b>

    {products.map((product, index) => <p key={product.name}>{product.productName}:{product.description}:{product.categories}: {product.price}<button key="{product.id}{product.name}"  type="button" onClick={()=>deleteProductClick(index)}>X</button></p>)}

    <button type="button" onClick={addProductClicked}>Add Product</button>
    {getCategory().map(category => <label><input type="checkbox" value={category} onChange={(e)=>handleChange(e)}/>{category}</label>)}

</div>)

} 
export default ProductStore
