const Product = (props) => {

    const product = props.product
    const {id, productName,description, canExpire,expiryDate, category, price, special} = product;

    return(

        <productpod>
            <p align="center"><img className="productimage" src= {imgURL}  onError={onImageError} alt={id} onClick={openModal}/>

                {showModal ? <ProductDetail setShowModal={setShowModal} setVariantId={id} brand={product._source.channel}/> : null}
                </p>
              <p>
                Name: <strong>{productName}</strong>
              </p>
              <p>Description: {description}</p>
              <p>Expiry: {expiryDate || 'No Expiry'}</p>
              <p>Category: {category}</p>
              <p>Price: {price}</p> 
              <p>special: {special}</p>                
            </productpod>
    )
}
export default Product
