import React, { useRef } from "react";
import ReactDom from "react-dom";
import { useState, useEffect } from 'react'
import EditProductForm from "./EditProductForm" 

export const ProductDetails = ({ setShowModal, setVariantId, brand, setVariant }) => {  

  const [productData, setProductData] = useState([])


  useEffect(() => {
    console.log("PREM--------------------------------------" + setVariantId);
    getProductDetails(brand, setVariantId).then(json => {
      setProductData(json)
    })

  }, [])

  const modalRef = useRef();
  console.log("Model Window is suppose to open." + setVariantId);

  const handleSelect = async (e) => { 
    e.preventDefault();

    console.log("e.target.value:" + e.target.value);

      const productData = getProductDetails(brand, setVariantId, e.target.value).then(json => {
        setProductData(json)
    });
  }




  const closeModal = (e) => {
    if (e.target === modalRef.current) {
      setShowModal(false);
    }
  };  



  return ReactDom.createPortal(
    <div className="container" ref={modalRef} onClick={closeModal}>
      <div className="modal">
      <button onClick={() => setShowModal(false)}>X</button>
      <EditProductForm/>
      </div>
    </div>,
    document.getElementById("portal")
  );
} 
export default EditProduct
