const Products = (props) => {

  console.log("Prem")
  console.log(props.products)
    const products = props.products

    console.log(products)


    let searchCount =  0;
    let results = '';
    console.log(JSON.stringify(searchResults));
    if (searchResults && searchResults?.total?.value) {
        console.log("PREM1" + searchResults);
        console.log("PREM2" + Object.keys(searchResults.total.value));
        console.log("PREM3" + searchResults.total.value);
        searchCount =  searchResults?.total?.value;
        results = searchResults.hits.map(product => <div className="item"><Product key={product._source.variantId} product={product} /></div>)
    }

    const content = results?.length ?  results : <article><p>No Matching Product</p></article>

    return (
        <main>
            <div className="messagebar"> <b>{searchCount} </b> products found... </div>
            <div className="grid">{content}</div>
        </main>
    )

    return(      
    <ul>
        {products.map((product, index) => {

          const {id, productName,description, canExpire,expiryDate, category, price, special} = product;
          return (
            <li key={id}>
              <div>
                Name: <strong>{productName}</strong>
              </div>
              <div>Description: {description}</div>
              <div>Expiry: {expiryDate || 'No Expiry'}</div>
              <div>Category: {category}</div>
              <div>Price: {price}</div> 
              <div>special: {special}</div>                
            </li>
          );
        })}
        {products.length === 0 && (
          <div>No product matching the filter</div>
        )}
      </ul>
    )
}
export default Products
