    const [showModal, setShowModal] = useState(false);
    const openModal = (productId) => {
        setShowModal(true);
      };

      const imageUL = "../images/no_image.png"

    return(

        <article>
            <p align="center"><img className="productimage" src= {imageURL}  alt={id} onClick={openModal}/>

                {showModal ? <EditProduct setShowModal={setShowModal} productId={id} /> : null}
                </p>
              <p>
                Name: <strong>{productName}</strong>
              </p>
              <p>Description: {description}</p>
              <p>Expiry: {expiryDate || 'No Expiry'}</p>
              <p>Category: {category}</p>
              <p>Price: {price}</p> 
              <p>special: {special}</p>                
            </article>
    )
}
export default Product
