import EditProduct from './EditProduct'
import {useState} from 'react'
import imageUrl from '../images/no_image.png'

const Product = (props) => {

    const product = props.product
    const {id, productName,description, canExpire,expiryDate, category, price, special} = product;

    const [showModal, setShowModal] = useState(false);
    const openModal = (productId) => {
        setShowModal(true);
      };


    return(
      <>
        <article>
            <p align="center"><img className="productimage" src= {imageUrl}  alt={id} onClick={openModal}/>

                {showModal ? <EditProduct setShowModal={setShowModal} productId={id} /> : null}
                </p>
              <p>
                Name: <strong>{productName}</strong>
              </p>
              <p>Description: {description}</p>
              <p>Expiry: {expiryDate || 'No Expiry'}</p>
              <p>Category: {category}</p>
              <p>Price: {price}</p> 
              <p>special: {special}</p>                       
            </article>
            <button>X</button> 
            </>
    )
}
export default Product
