import Product from './Product'
import EditProduct from './EditProduct'
import {useState, useEffect} from 'react'

const ProductList = ({products, deleteProductClick}) => {

  const productCount = products?.length || 0

  const [showModal, setShowModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState({});

  const openModal = (e) => {
      setShowModal(true);
      setSelectedProduct(e.target.value)
      console.log("PPPPPPPPPP")
      console.log(e.target.value)
    };

    useEffect( ()=> {
      setShowModal(false);
    }, [showModal, selectedProduct])

  return(
      <main>
            <div className="messagebar" key="product_found"> <b>{productCount} </b> products found... </div>
            <div className="grid">
                {
                    products.map((product, index) => (

                      <div className="item" key={index}>
                        <Product key={index} product={product}/>
                        <center><button onClick={(e) => openModal(e) } className="podButton" value={product}>Edit</button>
                        <button onClick={()=>deleteProductClick(product.productName)} className="podButton">Delete</button></center>                       
                      </div>

                    ))
                }
                {showModal ? <EditProduct setShowModal={setShowModal} selectedProduct={selectedProduct.productName}/>  : null}

                {products.length === 0 && (
                  <div>No product matching the filter</div>
                )}
          </div>
        </main>

    )
}
export default ProductList
