import { useSelector, useDispatch } from 'react-redux'
//import store from './store'
import { addProduct, removeProduct } from '../feature/product/productSlice'


const ProductStore = () => {

    const products = useSelector((state) => state.products)
    const dispatch = useDispatch()

    //console.log(products)

// Can still subscribe to the store
//store.subscribe(() => console.log(store.getState()))

// Still pass action objects to `dispatch`, but they're created for us


const onSavePostClicked = () => {

        dispatch(addProduct( { id: '9', productName: 'Mens T Shirt' }))   
  }


//dispatch(addProduct({ id: '10', productName: 'Jeans' }))

//dispatch(removeProduct({ id: '10', productName: 'Jeans' }))

return (<div><b>Products in the Store</b>

    {products.map(product => <p key={product.id}>{product.productName}:{product.description}: {product.price}</p>)}

    <button type="button" onClick={onSavePostClicked}>Add Product</button>

</div>)

} 
export default ProductStore
